mapper.insert:
insert into member(
member_email, member_pw, member_name, member_nick, member_gender, member_contact, member_birth
) values(
#{memberEmail}, #{memberPw}, #{memberName}, #{memberNick}, #{memberGender}, #{memberContact}, #{memberBirth}
)

mapper.update:
update member set 
member_login = sysdate 
where member_email = #{memberEmail}

mapper.select:
select * from(
	select rownum rn, TMP.*from(
		select * from member order by member_email asc
	)TMP
)where rn between #{begin} and #{end}

mapper.update:
update member
set
	member_nick = #{memberNick},
	member_contact = #{memberContact}
where member_email = #{memberEmail}
